    public static class CaptionTransitionHelper {

        private static string __ResStr(string name, string defaultValue, params object[] parms) { return ResourceAccess.GetResourceString(typeof(CaptionTransitionHelper), name, defaultValue, parms); }

        public class CaptionTransition {

            public const string NONE = "NONE";
            public const string DEFAULT = "DEFAULT";

            public string Effect { get; set; }
            public string Name { get; set; }
        };
#if MVC6
        public static MvcHtmlString RenderCaptionTransition(this IHtmlHelper htmlHelper, string name, string selection, object HtmlAttributes = null)
#else
        public static MvcHtmlString RenderCaptionTransition(this HtmlHelper htmlHelper, string name, string selection, object HtmlAttributes = null)
#endif
        {

            List<SelectionItem<string>> list = (from t in CaptionTransitions select new SelectionItem<string>() {
                Text = t.Name,
                Value = t.Effect,
            }).ToList();

            bool useNone = htmlHelper.GetControlInfo<bool>("", "None");
            if (useNone)
                list.Insert(0, new SelectionItem<string> {
                    Text = __ResStr("none", "(None)"),
                    Tooltip = __ResStr("noneTT", "No transition"),
                    Value = CaptionTransition.NONE,
                });
            bool useDefault = ! htmlHelper.GetControlInfo<bool>("", "NoDefault");
            if (useDefault)
                list.Insert(0, new SelectionItem<string> {
                    Text = __ResStr("default", "(Slider Default)"),
                    Tooltip = __ResStr("defaultTT", "Use the slider's default transition"),
                    Value = CaptionTransition.DEFAULT,
                });

            return htmlHelper.RenderDropDownSelectionList(name, selection, list, HtmlAttributes: HtmlAttributes);
        }

